import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'package:untitled/controller/shop_bucket_controller.dart';
import 'package:untitled/widget/shop_bucket.dart';

class ShopBucket extends StatelessWidget {
  final shopBucketController = Get.find<ShopBucketController>();

  @override
  Widget build(BuildContext context) {
    return SafeArea(
        child: Padding(
      padding: const EdgeInsets.all(10.0),
      child: Column(
        children: [
          Row(
            children: [
              SizedBox(),
              IconButton(onPressed: () {}, icon: Icon(Icons.arrow_back_ios)),
              Text('장바구니'),
            ],
          ),
          Container(
            height: 0.5,
            width: MediaQuery.of(context).size.width,
            color: Colors.black,
          ),
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              SelectAll(),
              TextButton(onPressed: (){}, child: const Text('선택삭제'))
            ],
          ),
          ListView.builder(
            itemCount: shopBucketController.shopBucketList.length,
              itemBuilder: (BuildContext ctx, int idx){
              return ShopBucketTile(
                itemPrise: 1,
                itemName: '11',
                marketName: 'cucu',
                networkImage: 'data:image/jpeg;base64,',
              );
              }
          )
        ],
      ),
    ));
  }
}

class SelectAll extends StatelessWidget {
  RxBool isValue = false.obs;

  @override
  Widget build(BuildContext context) {
    return Row(
      children: [
        Obx(() => Checkbox(
            value: isValue.value == true ? true : false,
            onChanged: (values) {
              isValue.value = values != true ? true : false;
            })),
        const Text('전체선택'),
      ],
    );
  }
}
